DROP TABLE IF EXISTS XWCCSYSPARM;
create table XWCCSYSPARM
(
    GROUPREFID          CHAR(10) NOT NULL,
    CATEGORY            CHAR(20) NOT NULL,
    KEY_ID              CHAR(50) NOT NULL,
    SEQ_NUM             INTEGER  NOT NULL,
    EFF_START_DT        DATE,
    EFF_END_DT          DATE,
    DATA_VALUE          VARCHAR(500),
    LAST_UPDATE_DT      TIMESTAMP,
    LAST_UPDATE_USER    VARCHAR(20),
    DESCRIPTION         VARCHAR(255),
    CONSTRAINT XWCCSYSPARM_PK PRIMARY KEY (GROUPREFID, CATEGORY, KEY_ID, SEQ_NUM)
);


DROP TABLE IF EXISTS ITEM;
create table ITEM
(
    ID          CHARACTER VARYING not null,
    DESCRIPTION CHARACTER VARYING,
    constraint ITEM_PK
        primary key (ID)
);

DROP TABLE IF EXISTS ADAPTER_DET;
create table ADAPTER_DET
(
    ADAPTER_ID          VARCHAR(25) NOT NULL,
    ADAPTER_NAME        VARCHAR(255) NOT NULL,
    DESCRIPTION         VARCHAR(255),
    STATUS              CHAR(1) NOT NULL,
    END_DT              TIMESTAMP,
    LAST_UPDATE_DT      TIMESTAMP,
    LAST_UPDATE_USER    VARCHAR(20),
    CONSTRAINT ADAPTER_DET_PK PRIMARY KEY (ADAPTER_ID)
);


